$(document).ready(function(){	$('.slider-image').click(function(e){		e.preventDefault();		var slider_url = $(this).attr('src');		var pos2 = slider_url.lastIndexOf('/');		var file = slider_url.substr(pos2+1);		var url = $('img.big-image').attr('src');		var pos1 = url.lastIndexOf('/');		var src = url.substring(0,pos1+1);		$('.big-image-container img').fadeOut("slow", function(){			$('.big-image-container img').attr('src', src+file);			$('.big-image-container img').fadeIn("slow");		});	});});